	c.accordions = {};
	c.accordions.tab_2_A1 = false;
	c.accordions.tab_2_A2 = false;
	c.accordions.tab_2_A3 = false;
	c.accordions.tab_2_A4 = false;


	c.current_form_idx = 'Introduction';

	$scope.data.hideRelatedLists = true;

	c.startForm = function(){
		c.showView(2);
	}

	c.getFormValues = function() {
		var g_form = c.g_forms[c.current_form_idx];
		var fields = g_form.getFieldNames();
		var record = {};
		fields.forEach(function(field) {
			var f = g_form.getField(field);
			if(f.type == 'glide_date'){
				var dt_str = g_form.getValue(field);
				var d = new Date(dt_str);
				record[field] = d;
			}
			else{
				record[field] = g_form.getValue(field);
			}


			if(field == 'gender'){
				c.data.sex = g_form.getValue(field);
			}

		})
		return record;		
	}

	c.saveForm = function(cb){
		var record = c.getFormValues();
		//also update progressing array
		var section = c.current_form_idx;
		if(c.data.saved_section.indexOf(section) == -1){
			c.data.saved_section.push(section);
		}
		record.saved_section = c.data.saved_section.length == 0 ? '' : c.data.saved_section.join(',');
		$scope.server.get({
			action: 'update',
			record: record,
			sys_id: c.data.sys_id
		}).then(function(){
			var g_form = c.g_forms[c.current_form_idx];
			g_form.$private.userState.clearModifiedFields();
			/*
			spModal.open({
				title: "Message",
				message: "<center>Record has been saved successfully.</center>",
				buttons: [{label: 'OK', primary: true}]
			})
			*/
			if(cb){
				cb();
			}
		});	
	}

	c.submitForm = function(){
		c.saveForm(function(){
			//$('#thank_you').show();
		});
	}

	c.previousForm = function(){
		if(c.show_view_index == 2){
			if(c.show_sub_view_index == 1){
				c.showView(c.show_view_index - 1);
			}
			else{
				c.showView(c.show_view_index, 'tab_2_A' + (c.show_sub_view_index - 1));
			}

		}
		else{
			c.showView(c.show_view_index - 1);
		}
	}

	c.saveAndNextForm = function(){
		c.saveForm(function(){
			if((c.show_view_index == 10 || c.show_view_index == 11 || c.show_view_index == 12 ) && c.data.sex == 'MALE'){
				c.show_view_index = 13;
			}
			if(c.show_view_index == 2){
				if(c.show_sub_view_index == 4){
					c.showView(c.show_view_index + 1);
				}
				else{
					c.showView(c.show_view_index, 'tab_2_A' + (c.show_sub_view_index + 1 ));
				}
			}
			else{
				c.showView(c.show_view_index + 1);
			}

		});
	};

	c.openAccordion = function(index, acc){
		//c.showView(c.show_view_index, acc);
		c.showView(index, acc);
	};

