	c.setActions = function(view, widget_idx){
		var button_bar = $('.right-side-content #form_widget_'+ widget_idx +' div.panel-footer');
		button_bar.html("");
		var html = '';
		if(view == 'SignatureAndSubmit'){
			html += '<div><button type="submit" ng-click="c.submitForm()" class="btn btn-primary pull-right action-btn ng-scope" style="background-color:green; padding: 5px 25px;">Submit</button>';
		}
		else{
			html += '<div><button type="submit" ng-click="c.saveAndNextForm()" class="btn btn-primary pull-right action-btn ng-scope" style=" padding: 5px 25px;">Save & Next</button>';
		}
		//html += '<button type="submit" ng-click="c.saveForm()" class="btn btn-primary pull-right action-btn ng-scope" style="padding: 5px 25px;">Save</button></div>';
		html += '<button type="submit" ng-click="c.previousForm()" class="btn btn-primary pull-right action-btn ng-scope" style="padding: 5px 25px;">Back</button></div>';
		var content = $compile(html)($scope)[0];
		button_bar.append(content);
	};


	$scope.$on('spModel.fields.rendered', function(e, gFormInstance) {
		var g_form = null;
		if(e.targetScope.formModel.view == "SectionA1"){
			c.setActions("SectionA1", "2_A1");
			c.g_forms['SectionA1'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionA2"){
			c.setActions("SectionA2", "2_A2");
			c.g_forms['SectionA2'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionA3"){
			c.setActions("SectionA3", "2_A3");
			c.g_forms['SectionA3'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionA4"){
			c.setActions("SectionA4", "2_A4");
			c.g_forms['SectionA4'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionB1"){
			c.setActions("SectionB1", "3");
			c.g_forms['SectionB1'] = e.targetScope.getGlideForm();
			g_form = e.targetScope.getGlideForm();	
			var have_biopsy = g_form.getValue('have_biopsy');
			if(have_biopsy == 'YES'){
				c.biopsyDisplay = true;
			}
			else{
				c.biopsyDisplay = false;
			}
			var diagnosed_with_any_cancer = g_form.getValue('diagnosed_with_any_cancer');
			if(diagnosed_with_any_cancer == 'YES'){
				c.cancerDisplay = true;
			}
			else{
				c.cancerDisplay = false;
			}

			e.targetScope.$on('field.change.have_biopsy', function(ev, obj) {
				if(obj.newValue == 'YES'){
					c.biopsyDisplay = true;
				}
				else{
					c.biopsyDisplay = false;
				}
			});
			e.targetScope.$on('field.change.diagnosed_with_any_cancer', function(ev, obj) {
				if(obj.newValue == 'YES'){
					c.cancerDisplay = true;
				}
				else{
					c.cancerDisplay = false;
				}
			});
		}
		else if(e.targetScope.formModel.view == "SectionB2"){
			c.setActions("SectionB2", "4");
			c.g_forms['SectionB2'] = e.targetScope.getGlideForm();
			g_form = e.targetScope.getGlideForm();	
			var have_any_surgery = g_form.getValue('have_any_surgery');
			if(have_any_surgery == 'YES'){
				c.surgeryDisplay = true;
			}
			else{
				c.surgeryDisplay = false;
			}

			e.targetScope.$on('field.change.have_any_surgery', function(ev, obj) {
				if(obj.newValue == 'YES'){
					c.surgeryDisplay = true;
				}
				else{
					c.surgeryDisplay = false;
				}
			});
		}
		else if(e.targetScope.formModel.view == "SectionB3"){
			c.setActions("SectionB3", "5");
			c.g_forms['SectionB3'] = e.targetScope.getGlideForm();
			g_form = e.targetScope.getGlideForm();	
			var take_more_than_6_months = g_form.getValue('take_more_than_6_months');
			var taking_any_prescription_medication = g_form.getValue('taking_any_prescription_medication');
			if(take_more_than_6_months == 'YES' || taking_any_prescription_medication == 'YES'){
				c.medicationDisplay = true;
			}
			else{
				c.medicationDisplay = false;
			}

			e.targetScope.$on('field.change.take_more_than_6_months', function(ev, obj) {
				var v1 = c.g_forms['SectionB3'].getValue('take_more_than_6_months');
				var v2 = c.g_forms['SectionB3'].getValue('taking_any_prescription_medication');
				if(v1 == 'YES' || v2 == 'YES'){
					c.medicationDisplay = true;
				}
				else{
					c.medicationDisplay = false;
				}
			});

			e.targetScope.$on('field.change.taking_any_prescription_medication', function(ev, obj) {
				var v1 = c.g_forms['SectionB3'].getValue('take_more_than_6_months');
				var v2 = c.g_forms['SectionB3'].getValue('taking_any_prescription_medication');
				if(v1 == 'YES' || v2 == 'YES'){
					c.medicationDisplay = true;
				}
				else{
					c.medicationDisplay = false;
				}
			});
		}
		else if(e.targetScope.formModel.view == "SectionB4"){
			c.setActions("SectionB4", "6");
			c.g_forms['SectionB4'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionC"){
			c.setActions("SectionC", "7");
			c.g_forms['SectionC'] = e.targetScope.getGlideForm();
			g_form = e.targetScope.getGlideForm();
			var had_screening_for_cancer = g_form.getValue('had_screening_for_cancer');
			if(had_screening_for_cancer == 'YES'){
				c.screeningDisplay = true;
			}
			else{
				c.screeningDisplay = false;
			}

			e.targetScope.$on('field.change.had_screening_for_cancer', function(ev, obj) {
				var v1 = c.g_forms['SectionC'].getValue('had_screening_for_cancer');
				if(v1 == 'YES'){
					c.screeningDisplay = true;
				}
				else{
					c.screeningDisplay = false;
				}
			});
		}
		else if(e.targetScope.formModel.view == "SectionD"){
			c.setActions("SectionD", "8");
			c.g_forms['SectionD'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionE"){
			c.setActions("SectionE", "9");
			c.g_forms['SectionE'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionF"){
			c.setActions("SectionF", "10");
			c.g_forms['SectionF'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionG1"){
			c.setActions("SectionG1", "11");
			c.g_forms['SectionG1'] = e.targetScope.getGlideForm();
		}
		else if (e.targetScope.formModel.view == "SectionG2"){
			c.setActions("SectionG2", "12");
			//Watch the Ever had your ovaries of fallopian tubes field for value changes
			e.targetScope.$on('field.change.ever_had_your_ovaries_or_fallopian_tubes_removed', function(ev, obj) {
				var g2 = c.g_forms['SectionG2'].getValue('ever_had_your_ovaries_or_fallopian_tubes_removed');
				//If yes, display reproductive surgery history table
				if(g2 == 'yes'){
					c.reproDisplay = true;
				}
				else{
					c.reproDisplay = false;
				}

			});

			//Watch have you ever had your breast removal field
			e.targetScope.$on('field.change.have_you_ever_had_your_breast_s_removed_to_reduce_the_risk_of_breast_cancer', function(ev, obj) {
				var g3 = c.g_forms['SectionG2'].getValue('have_you_ever_had_your_breast_s_removed_to_reduce_the_risk_of_breast_cancer');

				//If yes, display widget to add breast surgery history table
				if(g3 == 'yes'){
					c.breastDisplay = true;
				} else {
					c.breastDisplay = false;
				}
			});

			e.targetScope.$on('field.change.were_you_ever_prescribed_female_hormones_around_the_time_of_menopause', function(ev, obj) {
				var g4 = c.g_forms['SectionG2'].getValue('were_you_ever_prescribed_female_hormones_around_the_time_of_menopause');

				//If yes, display widget to add breast surgery history table
				if(g4 == 'yes'){
					c.hormoneDisplay = true;
				} else {
					c.hormoneDisplay = false;
				}
			});


			c.g_forms['SectionG2'] = e.targetScope.getGlideForm();
			g_form = e.targetScope.getGlideForm();
			var reproRemoved = g_form.getValue('ever_had_your_ovaries_or_fallopian_tubes_removed');
			var breastRemoved = g_form.getValue('have_you_ever_had_your_breast_s_removed_to_reduce_the_risk_of_breast_cancer');
			var hormones = g_form.getValue('were_you_ever_prescribed_female_hormones_around_the_time_of_menopause');
			if(reproRemoved == 'yes'){
				c.reproDisplay = true;
			} else {
				c.reproDisplay = false;
			}
			if (breastRemoved == 'yes'){
				c.breastDisplay = true;
			}else{
				c.breastDisplay = false;
			}
			if (hormones == 'yes'){
				c.hormoneDisplay = true;
			} else {
				c.hormoneDisplay = false;
			}
		}
		else if(e.targetScope.formModel.view == "SectionG3"){
			c.setActions("SectionG3", "13");
			c.g_forms['SectionG3'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionH"){
			c.setActions("SectionH", "14");
			c.g_forms['SectionH'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionH1"){
			c.setActions("SectionH1", "15");
			c.g_forms['SectionH1'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionH2"){
			c.setActions("SectionH2", "16");
			c.g_forms['SectionH2'] = e.targetScope.getGlideForm();
		}
		else if(e.targetScope.formModel.view == "SectionH3"){
			c.setActions("SectionH3", "17");
			c.g_forms['SectionH3'] = e.targetScope.getGlideForm();
		}
		else{
			c.setActions("SignatureAndSubmit", "18");
			c.g_forms['SignatureAndSubmit'] = e.targetScope.getGlideForm();
		}
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var sys_id = str.get('sys_id');
	data.sys_id = sys_id;
	//data.record.sex = '';


	if(input){
		if(input.action == 'update'){
			var record = input.record;
			var gr1 = new GlideRecord('x_naci_family_coho_individual_information_questionnaire');
			gr1.get(input.sys_id);
			for (var key in record) {
				var value = record[key];
				gr1.setValue(key, value);
			}
			//data.sex = gr1.gender.toString();
			gr1.updateMultiple();

		}

	}

	var enrollmentLookUp = new GlideRecord ('x_naci_family_coho_individual_information_questionnaire');
	if (enrollmentLookUp.get(sys_id)){
		data.sex = enrollmentLookUp.gender.toString();
		var tmp = enrollmentLookUp.saved_section.toString();
		data.saved_section = tmp == '' ? [] : tmp.split(',');
	}
})();
