sp_widget.client_script:
function($scope,spModal, $rootScope, $location) {
  var c = this;
	var s = $location.search();

	//c.intake_sys_id = 'f46149a8db940850b86770c08c9619ba';
	c.intake_sys_id = c.data.intake_sys_id;
	c.fhq_sys_id = c.data.fhq_sys_id;
	c.iiq_sys_id = c.data.iiq_sys_id;
	c.view_model = "proband";
	c.openLFS = function(){

	};

	c.openPPB = function(){

	};

	c.openIBMFS = function(){

	};

	c.openFM = function(){

	};

	c.openRAS = function(){

	};

	c.switch2ProbandView = function(){
		c.view_model = "proband";
	}

	c.switch2FamilyMemberView = function(){
		c.view_model = "family_member";
	}

	c.reviewIntakeForm = function(sys_id){
		s.id = 'fcsms_intake_form';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}

	c.reviewStudyConsent = function(sys_id){
		s.id = 'fcsms_consent';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}

	c.reviewStudyMRRConsent = function(sys_id){
		s.id = 'fcsms_mrr_consent';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}

	c.reviewFHQ = function(sys_id){
		s.id = 'fcsms_fhq';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}

	c.reviewIIQ = function(sys_id){
		s.id = 'fcsms_iiq';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}


}

