sp_widget.client_script:
function($rootScope, $scope, $timeout, spModal, spUtil) {
  /* widget controller */
  var c = this;

	$rootScope.$on('spModel.fields.rendered', function(event){
		var formLabel = event.targetScope.formModel.label
		if (!/Family History Details/.test(formLabel))
				return;
	  if (e.targetScope.formModel.view == 'FHG')
			c.g_form = e.targetScope.getGlideForm();			

		$timeout(function(){
			$('<h4 class="mb-0"><b>Answer only if person is dead<b></h4><hr/>').insertBefore($('#date_of_death').closest('.row'))
		});
	});

	c.openForm = function(){
		$('#addFamilyMemberDiv').modal('show');
		spUtil.get('widget-form', {
			"sys_id": "-1",
			"table": "x_naci_family_coho_family_history_details",
			"view": "FHD"
		}).then(function(widget) {
			c.data.widget_add = widget;
		})
	}

	c.cancelForm = function(){
		$('#addFamilyMemberDiv').modal('hide');
	};

	c.saveForm = function(){
		var fields = c.g_form.getFieldNames();
		var record = {};
		fields.forEach(function(field) {
			var f = c.g_form.getField(field);
			if(f.type == 'glide_date'){
				var dt_str = c.g_form.getValue(field);
				var d = new Date(dt_str);
				record[field] = d;
			}
			else{
				record[field] = c.g_form.getValue(field);
			}			
		})
		$scope.server.get({
			action: 'add',
			record: record
		}).then(function(){
			c.cancelForm();
		});
	}

	spUtil.recordWatch($scope, 'x_naci_family_coho_family_history_details', '', function(response){
		 update($scope);
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var sys_id = str.get('sys_id');
	data.id = sys_id;
	if(input){
		if(input.action == 'add'){
			var record = input.record;
			var gr1 = new GlideRecord('x_naci_family_coho_family_history_details');
			gr1.newRecord();
			for (var key in record) {
				gr1.setValue(key, record[key]);
			}
			gr1.intake_form = data.id;
			gr1.insert();
		}
		else if(input.action == 'delete'){
			var id = input.id;
			var gr2 = new GlideRecord('x_naci_family_coho_mock_up_intake_patient_cancer');
			gr2.get(id);
			gr2.deleteRecord();
		}
	}

	var gr = new GlideRecord('x_naci_family_coho_family_history_details');
	gr.addQuery('intake_form', data.id);
	gr.orderByDesc('sys_created_on');
	gr.query();
	data.records = [];
	while(gr.next()){
		var tmp = {};
		tmp.sys_id = gr.sys_id.toString();
		tmp.cancer_type = gr.cancer_type.toString();
		tmp.please_specify = gr.please_specify.toString();
		tmp.age_in_years_at_diagnosis = gr.age_in_years_at_diagnosis.toString();
		tmp.date_diagnosis = gr.date_diagnosis.toString();
		data.records.push(tmp);
	}
})();
