sp_widget.client_script:
function($scope, $location, $http) {
  /* widget controller */
  var c = this;
	var s = $location.search();
	loadScript('https://cdn.jsdelivr.net/npm/@pdf-lib/fontkit@0.0.4/dist/fontkit.umd.min.js');

	c.disabled = true;
	c.data.consent_be_contacted_genetic_variants = "yes";
	c.data.consent_be_contacted_genetic_changes = "yes";

	c.sign_date = c.data.sign_date;
	c.sign_user_name = c.data.user_name;

	c.data.signature_password = '';
	c.errorConsent = '';

	document.getElementById('pdf-iframe').src = '/api/naci/attachment_util/attachment/v1/' + c.data.pdf_id ;

	var containerEl = $("#pdf-content")[0];

	$("#pdf-content").scroll(function () {
		if (containerEl.scrollTop + containerEl.clientHeight >= containerEl.scrollHeight) {
			c.disabled = false;
			$scope.$apply();
		}
		else{
			c.disabled = true;
			$scope.$apply();
		}
	});


	c.toConsent = function(){
		c.data.signature_password = '';
		c.errorConsent = '';
		$('#consent-modal').modal('show');
	}

	c.cancelConsent = function(){
		$('#consent-modal').modal('hide');
	}

		c.generatePDF = function(){
		  var documentId = 'ad5f914cdb110810b86770c08c9619b9';
		  var url = '/sys_attachment.do?sys_id=' + documentId;
			var doc;
			return fetch(url).then(function(response) {
				return response.arrayBuffer();
			}).then(function(bytes) {
				return PDFLib.PDFDocument.load(bytes);
			}).then(function(document) {
				doc = document;
				doc.registerFontkit(fontkit)
				return doc.embedFont(getCursiveFont());
			}).then(function(font) {
				var pages = doc.getPages();
				var lastPage = pages[pages.length - 1];
				//this is for adult signature, the guardian's signature is on the different location of PDF 
				//we need to determine if the signer is proband or guardian.
				lastPage.drawText(c.data.user_name, {
					x: 200,
					y: 300,
					size: 12
				});

				lastPage.drawText(c.data.birth_day, {
					x: 420,
					y: 300,
					size: 12
				});				

				lastPage.drawText(c.data.user_name, {
					x: 180,
					y: 260,
					size: 18,
					font: font
				});

				lastPage.drawText(c.data.signature_date, {
					x: 420,
					y: 260,
					size: 12
				});

				return doc.save();
			}).then(function(pdfBytes){
				return arrayBufferToBase64(pdfBytes);
			});
	};

	c.consentForm = function(){
		var study_id = '5bf67315db3f3f4054d8ff621f961968';
		var param = {};
		param.sign_date = c.sign_date;
		param.sign_user_name = c.sign_user_name;
		param.signature_password = c.data.signature_password;
		param.study_id = study_id;
		param.type = 'MRR';
		$http({
				url: '/api/x_naci_family_coho/fcsms_apis/validateOKTAUser',
				 method : "POST",
				 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', c.data.session_id); },
				 headers: {'Content-Type': 'application/json'},
				 data: JSON.stringify(param),
				 processData: false
			}).then(function(res){
				var result = res.data.result;
				if(result.status != 'SUCCESS'){
					c.errorConsent = "Please enter the correct password to sign.";
				}
			  else{
					c.generatePDF().then(function(base64) {
						var pm = {};
						pm.fileContents = base64;
						pm.sys_id = result.sys_id;
						pm.type = 'mrr';
						$http({
								url: '/api/x_naci_family_coho/fcsms_apis/attachSignedPDF',
								 method : "POST",
								 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', c.data.session_id); },
								 headers: {'Content-Type': 'application/json'},
								 data: JSON.stringify(pm),
								 processData: false
							}).then(function(res){
								var result = res.data.result;
								$('#consent-modal').modal('hide');
								s.id = "fcsms_submission_acknowledge_page";
								s.form = "mrr_consent";
								$location.search(s).replace();
								console.log(result);
							},function(error){
							 $('#consent-modal').modal('hide');
								console.log(error);
							});
					});
				}
			},function(error){
			  $('#consent-modal').modal('hide');
				console.log(error);
			});
	}

	function arrayBufferToBase64( buffer ) {
			var binary = '';
			var bytes = new Uint8Array( buffer );
			var len = bytes.byteLength;
			for (var i = 0; i < len; i++) {
					binary += String.fromCharCode( bytes[ i ] );
			}
			return window.btoa( binary );
	}	

	c.cancelForm = function(){
		s.id = 'fcsms_patient_home_page';
		s.sys_id = null;
		$location.search(s).replace();
	}

	function loadScript(url) {
		var script = document.createElement('script');
		script.src = url;
		document.body.appendChild(script);
	}

	function getCursiveFont() {
		return 'data:font/opentype;base64,'
	}

}

