sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var intake_sys_id = str.get('sys_id');
	data.session_id = gs.getSession().getSessionToken();
	data.user_name = gs.getUserDisplayName();
	data.pdf_id = '';
	var attachment =  new GlideRecord('sys_attachment');
	attachment.addQuery('table_sys_id','1731aa16db89001054d8ff621f961914');
	attachment.query();
	attachment.next();
	try {
		data.pdf_id = attachment.sys_id.toString();
	}
	catch(err) {
		console.log(err);
	}
	data.sign_date = '';
	var months = {
		0: 'January',
		1: 'February',
		2: 'March',
		3: 'April',
		4: 'May',
		5: 'June',
		6: 'July',
		7: 'August',
		8: 'September',
		9: 'October',
		10: 'November',
		11: 'December'
	}
	var today = new Date();
  var year = today.getFullYear();
	var date = today.getDate();
	var month = months[today.getMonth()];
	data.sign_date = month + ' ' + date + ', ' + year;
	var s_month = today.getMonth() <= 8 ? '0' + (today.getMonth() + 1) : today.getMonth() + 1; 
	data.signature_date = s_month + '/' + date + '/' + year;

	var gr = new GlideRecord('x_naci_family_coho_mock_up_intake');
	gr.get(intake_sys_id);
	var birthday = gr.birthday.toString();
  if(birthday != null && birthday.trim().length > 0){
		var temp = birthday.split('-');
		data.birth_day = temp[1]+'/'+temp[2]+'/'+temp[0];
	}else{
		data.birth_day = '';
	}

})();
