sp_widget.client_script:
function($scope, $timeout, spUtil, $uibModal, spModal) {
  /* widget controller */
  var c = this;
	c.forms = {};
	c.sections = [];	
	c.forms = {};

	$scope.$on('spModel.fields.rendered', function(event) {
	  var g_form = event.targetScope.getGlideForm()
		var view = event.targetScope.formModel.view;
		c.forms[view] = g_form;
		$timeout(function(){
			renderAnnotation('.annotation.aafddc21db1c0c50b86770c08c961915', '','h3', true);
			//renderAnnotation('.annotation.29e54193db40c050b86770c08c9619d5', 'What is the name, office address, and office phone number of your regualr doctor?', 'h3')
			//renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '<br/>', 'h3')
		});
		if(view == 'FHQ2'){
			$timeout(function(){
				$('input[name="birthday"]').prop('readonly',true);		
			});
		}
	 });

	$scope.loadForm = function(index){
		var widgetName = 'widget_' + index;
		var view = 'FHQ' + index;
		if (!c.data[widgetName] && index != 5) {
			spUtil.get('widget-form', {
				sys_id: c.data.id,
				table: "x_naci_family_coho_family_history_question",
				view: view
			}).then(function(widget) {
				c.data[widgetName] = widget;
				if(index == 3){
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.29e54193db40c050b86770c08c9619d5', 'What is the name, office address, and office phone number of your regualr doctor?', 'h3');
						});
					});
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '', 'h3');
						});
					});					
				}
/*				if(index == 4){
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '555<br/>', 'h3');
						});
					});
				} */
			})
		}

	}

	$scope.saveForm = function(form){
		var modal = $uibModal.open({
			template: $('#saving-data-modal')[0].innerHTML,
			size: 'sm'
		});
		var record = getFormValues(form);
		$scope.server.get({
			action: 'update',
			record: record,
			sys_id: c.data.id
		}).then(function(){
			$('#loading').hide();
			modal.close();
			spModal.open({
				title: "Message",
				message: "<center><b>Record has been updated successfully</b><center>",
				buttons: [{label: 'OK', primary: true}]
			})
		});			
	}

	$scope.submitForm = function(){
		alert("Submit Form");
	}

	function getFormValues(gForm) {
		var fields = gForm.getFieldNames();
		var record = {};
		fields.forEach(function(field) {
			var f = gForm.getField(field);
			if(f.type == 'glide_date'){
				var dt_str = gForm.getValue(field);
				var d = new Date(dt_str);
				record[field] = d;
			}
			else{
				record[field] = gForm.getValue(field);
			}
		})
		return record;		
	}
}

