sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var enrollment_sys_id = str.get('sys_id');
	var gr = new GlideRecord('x_naci_family_coho_family_history_question');
	gr.addQuery('enrollment',enrollment_sys_id);
	gr.query();
	gr.next();
	var sys_id = gr.sys_id.toString();
	data.id = sys_id;

	if(input){
		if(input.action == 'update'){
			var record = input.record;
			var gr1 = new GlideRecord('x_naci_family_coho_family_history_question');
			gr1.get(input.sys_id);
			for (var key in record) {
				gr1.setValue(key, record[key]);
			}
			gr1.updateMultiple();
		}
	}

	data.widget_1 = $sp.getWidget('widget-form', {
		"sys_id": sys_id,
		"table": "x_naci_family_coho_family_history_question",
		"view": "FHQ1"
	});

})();
