sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var enrollment_sys_id = str.get('sys_id');
	var gr = new GlideRecord('x_naci_family_coho_family_history_question');
	gr.addQuery('enrollment',enrollment_sys_id);
	gr.query();
	gr.next();
	var sys_id = gr.sys_id.toString();
	data.widget_1 = $sp.getWidget('widget-form', {
		"sys_id": sys_id,
		"table": "x_naci_family_coho_family_history_question",
		"view": "FHQ1"
	});
	data.widget_2 = $sp.getWidget('widget-form', {
		"sys_id": sys_id,
		"table": "x_naci_family_coho_family_history_question",
		"view": "FHQ2"
	});	
	data.widget_3 = $sp.getWidget('widget-form', {
		"sys_id": sys_id,
		"table": "x_naci_family_coho_family_history_question",
		"view": "FHQ3"
	});	
	data.widget_4 = $sp.getWidget('widget-form', {
		"sys_id": sys_id,
		"table": "x_naci_family_coho_family_history_question",
		"view": "FHQ4"
	});	
})();
