sp_widget.client_script:
function($scope, $timeout, spUtil) {
  /* widget controller */
  var c = this;
	c.forms = {};
	c.sections = [];	

	$scope.$on('spModel.fields.rendered', function(event) {
		$timeout(function(){
			renderAnnotation('.annotation.aafddc21db1c0c50b86770c08c961915', '','h3', true);
			//renderAnnotation('.annotation.29e54193db40c050b86770c08c9619d5', 'What is the name, office address, and office phone number of your regualr doctor?', 'h3')
			//renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '<br/>', 'h3')
		});
	 });

	$scope.loadForm = function(index){
		var widgetName = 'widget_' + index;
		var view = 'FHQ' + index;
		if (!c.data[widgetName] && index != 5) {
			spUtil.get('widget-form', {
				sys_id: c.data.id,
				table: "x_naci_family_coho_family_history_question",
				view: view
			}).then(function(widget) {
				c.data[widgetName] = widget;
				if(index == 3){
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.29e54193db40c050b86770c08c9619d5', 'What is the name, office address, and office phone number of your regualr doctor?', 'h3');
						});
					});
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '', 'h3');
						});
					});					
				}
/*				if(index == 4){
					$scope.$on('spModel.fields.rendered', function(event) {
						$timeout(function(){
							renderAnnotation('.annotation.ede54193db40c050b86770c08c9619f8', '555<br/>', 'h3');
						});
					});
				} */
			})
		}

	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
