sp_widget.client_script:
function($scope, $location,$http,spUtil) {
  /* widget controller */
  var c = this;
	var s = $location.search();

	c.data.label = "Personal Cancer";
	c.data.columns = [{label:"Cancer Type", name: "cancer_type"},{label:"If Other, Please Specify", name: "please_specify"},
										{label: "Age (in years) at Diagnosis",name: "age_in_years_at_diagnosis"},{label: "Date (mm/yyyy or yyyy) at Diagnosis",name: "date_at_diagnosis"}];

	c.openForm = function(){
		$('#addFamilyCancerDiv').modal('show');
	}

	c.cancelForm = function(){
		$('#addFamilyCancerDiv').modal('hide');
	};

	c.saveForm = function(){
		var req = {
		 method: 'POST',
		 url: '/api/x_naci_family_coho/personalcancer/savePersonalCancer',
		 headers: {
			 'Content-Type': 'application/json'
		 }
		}

		req.data = {};
		req.data.intake_form = "";

		$http(req).then(function(resp){
			var content = resp.data.result.content;
			$('#addFamilyCancerDiv').modal('hide');
		}, function(error_resp){
			console.log(error_resp);
		});
	}

	c.deleteCancer = function(sys_id){
		var req = {
		 method: 'POST',
		 url: '/api/x_naci_family_coho/personalcancer/deletePersonalCancer',
		 headers: {
			 'Content-Type': 'application/json'
		 }
		}

		req.data = {};
		req.data.sys_id = sys_id;

		$http(req).then(function(resp){
			var content = resp.data.result.content;
			$('#addFamilyCancerDiv').modal('hide');
		}, function(error_resp){
			console.log(error_resp);
		});
	}

	spUtil.recordWatch($scope, "x_naci_family_coho_mock_up_intake_patient_cancer", "", function(name, data) {  
			spUtil.update($scope);
	});

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.widget_add = $sp.getWidget('widget-form', {
		"sys_id": "-1",
		"table": "x_naci_family_coho_mock_up_intake_patient_cancer",
		"view": "Cancer"
	});

	var gr = new GlideRecord('x_naci_family_coho_mock_up_intake_patient_cancer');
	gr.query();
	data.records = [];
	while(gr.next()){
		var tmp = {};
		tmp.sys_id = gr.sys_id.toString();
		tmp.cancer_type = gr.cancer_type.toString();
		tmp.please_specify = gr.please_specify.toString();
		tmp.age_in_years_at_diagnosis = gr.age_in_years_at_diagnosis.toString();
		tmp.date_at_diagnosis = gr.date_at_diagnosis.toString();
		data.records.push(tmp);
	}
})();
