sp_widget.client_script:
function($scope, $location,spModal, spUtil) {
  /* widget controller */
  var c = this;
	var s = $location.search();

	c.data.label = "Fallopian/Ovaries Removal";

	c.data.columns = [
										{label:"Indicate what was removed", name: "type", width:'20%'},
										{label:"Indicate which side", name: "indicate_which_side", width:'20%'},
										{label:"Date removal occurred", name: "date_removal_occurred", width:'20%'},
										{label:"Age removal occurred", name: "age_removal_occurred", width:'20%'},
										{label: "Don't know",name: "dont_know", width:'20%'}										
										];

	c.openForm = function(){
		spUtil.get("widget-form", {
			"sys_id": "-1",
			"table": "x_naci_family_coho_individual_information_questionnaire_reproductive_removal",
			"view": 'reproductive'
		}).then(function(response) {
			response.data.hideRelatedLists = true;
			c.data.widget_add = response;
		})
		$('#repro_addDiv').modal('show');
	};

	c.cancelForm = function(){
		$('#repro_addDiv').modal('hide');
	};

	$scope.$on('spModel.fields.rendered', function(e) {
		if (e.targetScope.formModel.view == 'reproductive')
			c.g_form = e.targetScope.getGlideForm();	

	});

	c.getFormValues = function() {
		var g_form = c.g_form;
		var fields = g_form.getFieldNames();
		var record = {};
		fields.forEach(function(field) {
			var f = g_form.getField(field);
			if(f.type == 'glide_date'){
				var dt_str = g_form.getValue(field);
				var d = new Date(dt_str);
				record[field] = d;
			}
			else{
				record[field] = g_form.getValue(field);
			}

		})
		return record;		
	}

	c.saveForm = function(){
		var record = c.getFormValues();
		$scope.server.get({
			action: 'add',
			record: record
		}).then(function(){
			c.g_form.$private.userState.clearModifiedFields();
			c.cancelForm();
		});	
	}

	c.deleteBiopsy = function(sys_id){
		spModal.confirm("<div><center><b>Are you sure you want to delete this record?</b></center></div>").then(function(confirmed){
			if(confirmed){
				$scope.server.get({
					action: 'delete',
					id: sys_id
				})
			}
		})
	};

	spUtil.recordWatch($scope, "x_naci_family_coho_individual_information_questionnaire_reproductive_removal", "", function(name, data) {  
			spUtil.update($scope);
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var str = gs.action.getGlideURI();
	var sys_id = str.get('sys_id');
	data.sys_id = sys_id;

	/*
	data.widget_add = $sp.getWidget('widget-form', {
		"sys_id": "-1",
		"table": "x_naci_family_coho_individual_information_questionnaire_biospy",
		"view": "Biospy"
	});
	*/

	if(input){
		if(input.action == 'add'){
			var record = input.record;
			var gr1 = new GlideRecord('x_naci_family_coho_individual_information_questionnaire_reproductive_removal');
			gr1.newRecord();
			for (var key in record) {
				gr1.setValue(key, record[key]);
			}
			gr1.iiq_form = sys_id;
			gr1.insert();
		}
		else if(input.action == 'delete'){
			var id = input.id;
			var gr2 = new GlideRecord('x_naci_family_coho_individual_information_questionnaire_reproductive_removal');
			gr2.get(id);
			gr2.deleteRecord();
		}

	}


	data.records = [];
	var gr = new GlideRecord('x_naci_family_coho_individual_information_questionnaire_reproductive_removal');
	gr.addQuery('iiq_form',sys_id);
	gr.query();
	while(gr.next()){
		var tmp = {};
		tmp.sys_id = gr.sys_id.toString();
		tmp.type = gr.type.toString();
		tmp.age_removal_occurred = gr.age_removal_occurred.toString();
		tmp.date_removal_occurred = gr.date_removal_occurred.toString();
		tmp.indicate_which_side = gr.indicate_which_side.toString();
		tmp.dont_know = gr.dont_know.toString();
		data.records.push(tmp);
	}
})();
