sp_widget.client_script:
function($scope,spUtil, $timeout) {
	/* widget controller */
	var c = this;
	//spUtil.recordWatch($scope, "sc_req_item", "", function(name, data) {
	//	spUtil.update($scope);
  //});
	CreateChart('chart_total','Total Enrollments','pie');
	function CreateChart(container,title,chartType){
		var chart=	new Highcharts.Chart({ 

			chart: {  
				renderTo:container ,//'chart_sla',  
				plotBackgroundColor: null,
        plotBorderWidth: null,
        plotShadow: false,
				type: chartType  
			},
			title: {  
				text:title,
				style:{'font-weight':'bold'}
			},
			 tooltip: {
        pointFormat: '{series.name}: <b>{point.y}</b>'
       },
			plotOptions: {
				pie: {
					allowPointSelect: false,
          cursor: 'pointer',
					dataLabels: { enabled: false },
					showInLegend: true
				}
			},
			colors: ['2FA9E6','#2FE6D7','#FF7c60','#A32FE6'],
			series: c.data.pause_series//c.data.series


		},function(chart){
		});

	}

}

