sp_widget.client_script:
function($scope, $location,$http,spUtil) {
  /* widget controller */
  var c = this;
	var s = $location.search();

	c.data.label = "IIQ List";
	c.data.columns = [{label:"Name", name: "name"},{label:"Individual Type", name: "type"},
										{label:"IIQ Status", name: "iiq_status"},
										{label: "Last Update",name: "last_update"}];

	c.reviewIIQ = function(sys_id){
		s.id = 'fcsms_iiq';
		s.sys_id = sys_id;
		$location.search(s).replace();
	}

	var options = {
			filter: "active=true",
			table: "x_naci_family_coho_individual_information_questionnaire",
			view: "TestList"
	};

	c.showList = function(){
		spUtil.get('widget-data-table', options).then(function(response) {
			console.log(response);
				c.data.table = response;
		});
	}

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.records = [];
	var str = gs.action.getGlideURI();
	var enrollment_sys_id = str.get('sys_id');
	var gr = new GlideRecord('x_naci_family_coho_individual_information_questionnaire');
	gr.addQuery('enrollment',enrollment_sys_id);
	gr.query();
	while(gr.next()){
		var tmp = {};
		tmp.sys_id = gr.sys_id.toString();
		tmp.name = gr.name_of_proband.toString();
		tmp.type = gr.individual_type.toString();
		tmp.iiq_status = gr.iiq_status.toString();
		tmp.last_update = gr.last_update.toString();
		data.records.push(tmp);
	}

})();
