sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var regex = /^\d+$/;

	var nbrwn = g_form.getValue('current_age');

	if (nbrwn.search(regex) == -1) {

		g_form.setValue('current_age', '');
		g_form.showFieldMsg('current_age', 'This field accepts numeric values only', 'error');

	}

	var diagnosisIntVal = g_form.getIntValue('age_at_diagnosis');
	var ageIntVal = g_form.getIntValue('current_age');

	if (diagnosisIntVal > ageIntVal){
		g_form.setValue('current_age', '');
		g_form.showFieldMsg('current_age', 'Current age cannot be younger than the age of diagnosis', 'error');
	}

}
