sys_ui_policy.script_false:
function onCondition() {
	var fields = {
		'proband_last_name': true,
		'proband_first_name': true,
		'proband_middle_name': true,
		'proband_maiden_name': true,
		'proband_address': true,
		'proband_city': true,
		'proband_state': true,
		'proband_zipcode': true,
		'proband_home_phone': true,
		'proband_office_phone': true,
		'proxy_last_name': false,
		'proxy_first_name': false,
		'proxy_middle_name': false,
		'proxy_maiden_name': false,
		'proxy_address': false,
		'proxy_city': false,
		'proxy_state': false,
		'proxy_zipcode': false,
		'proxy_home_phone': false,
		'proxy_office_phone': false,
	};

	for (var key in fields) {
		g_form.setDisplay(key, fields[key]);
		g_form.setValue(key, null);
	}
}

sys_ui_policy.script_true:
function onCondition() {
	var falseFields = [
		'proband_last_name',
		'proband_first_name',
		'proband_middle_name',
		'proband_maiden_name',
		'proband_address',
		'proband_city',
		'proband_state',
		'proband_zipcode',
		'proband_home_phone',
		'proband_office_phone',
	];

	var trueFields = [
		'proxy_last_name',
		'proxy_first_name',
		'proxy_middle_name',
		'proxy_maiden_name',
		'proxy_address',
		'proxy_city',
		'proxy_state',
		'proxy_zipcode',
		'proxy_home_phone',
		'proxy_office_phone',
	];
	falseFields.forEach(function(field){
		g_form.setDisplay(field, false);
	});
	trueFields.forEach(function(field){
		g_form.setDisplay(field, true);
	});
}
