sys_ui_policy.script_false:
function onCondition() {
	var fields = [
		'cancer_type',
		'place_began',
		'diagnose_cancer_year',
		'diagnosed_doctor_last_name',
		'diagnosed_doctor_first_name',
		'diagnosed_doctor_middle_name',
		'diagnosed_doctor_address',
		'diagnosed_doctor_city',
		'diagnosed_doctor_state',
		'diagnosed_doctor_zipcode',
		'hospital_clinic_name',
		'hospital_clinic_street_address',
		'hospital_clinic_city',
		'hospital_clinic_state',
		'hospital_clinic_zipcode'
	];

	fields.forEach(function(field) {
		g_form.setDisplay(field, true);
		g_form.setValue(field, null);
	});

}

